mapper.delete:
DELETE FROM LECTRUE_COURSE
WHERE COURSE_ID = #{courseId}

    AND TEACHER_ID = #{teacherId}

mapper.select:
SELECT COURSE_HOUR
FROM COURCE
WHERE COURSE_ID = #{courseId}

SELECT LECTRUE_HOUR
FROM LECTRUE_COURSE
WHERE COURSE_ID = #{courseId} AND TEACHER_ID = #{teacherId}

mapper.update:
UPDATE COURCE
SET COURSE_HOUR = #{updatedCourseHour}
WHERE COURSE_ID = #{courseId}
