mapper.delete:
DELETE FROM LECTRUE_COURSE
WHERE TEACHER_ID = #{teacherId}

    AND COURSE_ID = #{courseId}

mapper.update:
UPDATE LECTRUE_COURSE
SET LECTRUE_HOUR = #{lectureHour}, LECTRUE_TERM = #{lectureTerm.value}
WHERE TEACHER_ID = #{teacherId} AND COURSE_ID = #{courseId}

mapper.select:
SELECT COURSE_HOUR
FROM COURCE
WHERE COURSE_ID = #{courseId}

SELECT SUM(LECTRUE_HOUR)
FROM LECTRUE_COURSE
WHERE COURSE_ID = #{courseId}
