mapper.update:
UPDATE PROJECTION
SET PROJ_NAME = #{projName}, PROJ_SRC = #{projSrc}, PROJ_TYPE = #{projType.value},
    PROJ_BALANCE = #{projBalance}, PROJ_START_YEAR = #{projStartYear}, PROJ_END_YEAR = #{projEndYear}
WHERE PROJ_ID = #{projId}

mapper.select:
SELECT PROJ_ID, PROJ_NAME, PROJ_SRC, PROJ_TYPE, PROJ_BALANCE, PROJ_START_YEAR, PROJ_END_YEAR
FROM PROJECTION
WHERE PROJ_ID = #{projId}

mapper.delete:
DELETE FROM IN_CHARGE
WHERE PROJ_ID = #{projId}

    AND TEACHER_ID = #{teacherId}

mapper.select:
SELECT TEACHER_ID, PROJ_ID, CHARGE_RANK, CHARGE_BALANCE
FROM IN_CHARGE

    WHERE PROJ_ID = #{projId}


    
        WHERE TEACHER_ID = #{teacherId}
    
    
        AND TEACHER_ID = #{teacherId}

SELECT COUNT(1) > 0
FROM IN_CHARGE
WHERE PROJ_ID = #{projId} 
  AND CHARGE_RANK = #{rank}
  AND TEACHER_ID != #{teacherId}

SELECT COALESCE(SUM(CHARGE_BALANCE), 0)
FROM IN_CHARGE
WHERE PROJ_ID = #{projId}
